mapper.select:
SELECT o.num, o.order_code, o.buyer_code, b.name as buyer_name, o.order_date, o.request_date, o.approval_date, o.return_date, o.status, o.memo, o.comment, o.delyn, o.adduser, o.adddate, o.upduser, o.upddate FROM order_header o LEFT JOIN buyer b ON o.buyer_code = b.buyer_code WHERE o.delyn = 'N' AND b.delyn = 'N' AND (o.status= "승인완료" OR o.status="승인요청")
AND o.order_code like #{orderCode}
AND o.buyer_code = #{buyerCode}
AND o.status = #{status}
AND (o.adduser = #{userId} OR o.upduser = #{userId})
