mapper.insert:
INSERT INTO order_header (
    order_code, buyer_code, order_date, request_date, status, memo, addUser
) VALUES (
    #{order_code}, #{buyer_code}, #{order_date}, #{request_date}, #{status}, #{memo}, #{adduser}
)

INSERT INTO order_item (
    order_code, product_code, quantity, currency, price, addUser
) VALUES (
    #{order_code}, #{product_code}, #{quantity}, 'Ïõê', #{price}, #{adduser}
)

mapper.select:
SELECT o.order_code, o.buyer_code, b.name as buyer_name, o.order_date, o.request_date, o.approval_date, o.return_date, o.status, o.memo, o.comment, o.delyn, o.adduser, o.adddate, o.upduser, o.upddate FROM order_header o LEFT JOIN buyer b ON o.buyer_code = b.buyer_code AND b.delyn = 'N' WHERE o.delyn = 'N'
AND o.order_code like #{orderCode}
AND o.buyer_code = #{buyerCode}
AND o.status = #{status}
AND (o.adduser = #{userId} OR o.upduser = #{userId})

mapper.update:
UPDATE order_item
SET quantity = #{quantity}, price = #{price},
    upduser = #{upduser}, upddate = CURRENT_TIMESTAMP()
WHERE order_code = #{order_code}
AND product_code = #{product_code}

UPDATE order_item
SET delyn = 'Y', upduser = #{upduser}, upddate = CURRENT_TIMESTAMP()
WHERE order_code = #{order_code}
AND product_code = #{product_code}

UPDATE order_header
SET status = #{status}, upduser = #{upduser}, upddate = CURRENT_TIMESTAMP()
WHERE order_code = #{order_code}
